# Function to display the main menu
def main():
    # Layout configuration
    sg.theme('BluePurple')

    layout = [
        [sg.Text('Overview', size=(20, 1), justification='left', font='Helvetica 20 bold')],
        [sg.Text('Scan Text: To start capturing text from PiCamera.', size=(45, 1), justification='left',
                 font='Helvetica 13')],
        [sg.Text('Import File: To convert image or text file into speech.', size=(45, 1), justification='left',
                 font='Helvetica 13')],
        [sg.Text('About: Information about the application and developers.', size=(45, 1), justification='left',
                 font='Helvetica 13')],
        [sg.Text('', size=(45, 1), justification='left', font='Helvetica 13')],
        [sg.Text('Text to Speech Engine', size=(20, 1), justification='left', font='Helvetica 15 bold')],
        [sg.Text('Google Text to Speech Online (gTTS)'),
         sg.Button('', image_data=toggle_btn_off, key='-TOGGLE-GRAPHIC-',
                   button_color=(sg.theme_background_color(), sg.theme_background_color()), border_width=0)],
        [sg.Text('Note: The program uses pyttsx by default, an offline text to speech library.', size=(60, 2),
                 justification='left', font='Calibri 9')],
        [sg.Button('Scan Text', size=(19, 3), font='Helvetica 10'),
         sg.Button('Import File', size=(19, 3), font='Helvetica 10'),
         sg.Button('About', size=(19, 3), font='Helvetica 10')],
    ]
    window = sg.Window('Optical Character Recognition', layout, location=(400, 750))

    # pttsx3 config
    engine = pyttsx3.init()
    engine.setProperty('rate', 125)
    engine.setProperty('voice', 'en-westindies')

    # Grid config
    num_rows = 9
    num_cols = 16
    font = cv2.FONT_HERSHEY_SIMPLEX
    font_scale = 1
    thickness = 2
    text_color = (255, 0, 0)
    text_position = (228, 510)

    # txt config on screen 
    textOnImage = 'Press "c" to capture image'
    textOnImage2 = 'Press "q" to close capture window'

    # OCR config
    config = ('-l eng --oem 3--psm 3 ')

    # variable initialization
    down = graphic_off = True
    openWindow = False
    gttsOn = False

    # Handle event
    while True:
        event, values = window.read(timeout=20)

        # event for exit
        if event == 'Exit' or event == sg.WIN_CLOSED:
            sys.exit()

        # event for  camera scan
        elif event == 'Scan Text':
            openWindow = True

        # event for import files
        elif event == 'Import File':
            window.close()
            importFiles(gttsOn)

        # event for toggles the tts config
        elif event == '-TOGGLE-GRAPHIC-':
            graphic_off = not graphic_off
            window['-TOGGLE-GRAPHIC-'].update(image_data=toggle_btn_off if graphic_off else toggle_btn_on)
            gttsOn = True

        # event for about
        elif event == 'About':
            window.close()
            aboutWindow()

        # Handle camera scan
        if openWindow:
            window.close()

            # PiCam initialization
            camera = PiCamera()
            camera.resolution = (912, 608)
            camera.framerate = 30
            rawCapture = PiRGBArray(camera, size=(912, 608))

            # PiCam config
            for frame in camera.capture_continuous(rawCapture, format="bgr", use_video_port=True):
                image = frame.array

                # Grid mapping
                grid_img = np.copy(image)
                height, width, _ = grid_img.shape
                row_step = int(height / num_rows)
                col_step = int(width / num_cols)

                for i in range(1, num_rows):
                    cv2.line(grid_img, (0, i * row_step), (width, i * row_step), (0, 255, 0), 1)

                for i in range(1, num_cols):
                    cv2.line(grid_img, (i * col_step, 0), (i * col_step, height), (0, 255, 0), 1)

                # show text on screen
                cv2.putText(grid_img, textOnImage, text_position, font, font_scale, text_color, thickness)
                cv2.putText(grid_img, textOnImage2, (170, 570), font, font_scale, text_color, thickness)

                # Frame config
                winname = "Capture Text"
                cv2.namedWindow(winname)
                cv2.moveWindow(winname, 100, 0)
                cv2.imshow(winname, grid_img)

                # cv2.imshow("Camera Scanner", image)

                # key config
                key = cv2.waitKey(1) & 0xff

                # Threshold
                gray = cv2.cvtColor(image, cv2.COLOR_BGR2GRAY)
                _, thresh = cv2.threshold(gray, 0, 255, cv2.THRESH_BINARY + cv2.THRESH_OTSU)

                # Image filter via gray scale
                kernel = cv2.getStructuringElement(cv2.MORPH_RECT, (1, 1))
                eroded = cv2.erode(thresh, kernel, iterations=2)
                dilated = cv2.dilate(eroded, kernel, iterations=2)

                # refresh data fetch
                rawCapture.truncate(0)

                # key capture condition
                if key == ord('c'):
                    try:
                        # Convert the scan img to text
                        text = pytesseract.image_to_string(dilated, config=config)

                        # using gtts as reader
                        if gttsOn:
                            print(text)
                            tts = gTTS(text)
                            tts.save("text.mp3")
                            mixer.init()
                            mixer.music.load("text.mp3")
                            mixer.music.play()
                            time.sleep(2)

                        # using pttsx3 as reader
                        else:
                            print(text)
                            cv2.destroyAllWindows()
                            engine.say(text)
                            engine.runAndWait()

                        print('Press "c" key to capture again!')
                        continue

                    except:
                        print('No text detected, press "c" key to capture again!')

                elif key == ord('q'):
                    camera.close()
                    cv2.destroyAllWindows()
                    main()

# img toggle 1
toggle_btn_off = b''
# img toggle 1
toggle_btn_on = b''

# call main function
main()